import React, { useEffect, useState } from "react";
import { getWinner } from "../utils/contractServices";
let winner;
const styles = {
    backgroundColor: 'rgb(153, 153, 153)',
    color: 'black',
    padding: '20px 40px',
    borderRadius: '5px',
    border: '2px',
    marginRight: '100px'
};
function GetWinnerButton() {
    const GetWinnerGame = async () => {
        try {
            const [winner, setWinner] = useState("0x00000");

            useEffect(() => {
                const fetchWinner = async () => {
                    const winnerG = await getWinner();
                    setWinner(winnerG);
                    console.log(winnerG);
                };

                fetchWinner();
            }, []);
        }
        catch (error) {
            console.error("Winner can't be retrieved...",error);    
        }
    };



    return(
    <div>
        <div>
            <button style={styles} onClick={GetWinnerGame}>Find out the winner!</button>
            <br></br>   
            <h1>Winner: {winner}</h1>
        </div>
    </div>
    );
}


export default GetWinnerButton;
